import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
declare var webkitSpeechRecognition: any;

@Injectable({
  providedIn: 'root'
})
export class VoiceRecognitionService {
  recognition: any;
  isListening = false;
  text = '';
  tempWords = '';
  transcript = new Subject<string>();

  constructor() {
    const { webkitSpeechRecognition }: IWindow = window as IWindow;
    this.recognition = new webkitSpeechRecognition();
    this.recognition.continuous = true;
    this.recognition.interimResults = true;
    this.recognition.lang = 'fr-FR';

    this.recognition.onresult = (event: any) => {
      const transcript = Array.from(event.results)
        .map((result: any) => result[0].transcript)
        .join('');
      this.tempWords = transcript;
    };

    this.recognition.onend = () => {
      this.wordConcat();
      this.isListening = false;
      console.log('End speech recognition');
    };
  }

  start() {
    if (!this.isListening) {
      this.recognition.start();
      this.isListening = true;
      console.log('Speech recognition started');
    } else {
      console.log('Recognition is already running');
    }
  }

  stop() {
    if (this.isListening) {
      this.recognition.stop();
      this.isListening = false;
      console.log('Speech recognition stopped');
    }
  }

  wordConcat() {
    if (this.tempWords.trim()) {
      this.text = `${this.text} ${this.tempWords}.`.replace(/\s+/g, ' ').trim();
      this.transcript.next(this.text);
      this.tempWords = '';
    }
  }

  getTranscript(): Observable<string> {
    return this.transcript.asObservable();
  }
}
